mapper.insert:
INSERT INTO BOARD
(
	  BOARD_IDXNO
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_NAME
)
VALUES
(
	  BOARD_IDXNO.NEXTVAL
	, #{boardTitle}
	, #{boardContent}
	, #{boardName}
)

mapper.update:
UPDATE BOARD SET
				  BOARD_TITLE = #{boardTitle}
				, BOARD_CONTENT = #{boardContent}
				, BOARD_NAME = #{boardName}
				, BOARD_REG_DATE = SYSDATE
WHERE BOARD_IDXNO = #{boardIdxno}
